import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Todo from './components/Todo';

function App() {
  const [text, setText] = useState("");
  const [todos, setTodos] = useState([]);
  const [isUpdating, setUpdating] = useState("");
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    axios.get("http://localhost:8080/get-todo")
      .then((res) => { setTodos(res.data) })
      .catch((err) => console.log(err));
  }, []);

  const addOrUpdateTodo = () => {
    if (text === "") {
      alert("Please write a ToDo before submitting.");
    } else if (text !== "" && isUpdating === "") {
      axios.post("http://localhost:8080/save-todo", { text })
        .then((res) => {
          setText("");
          setTodos([...todos, res.data]);
        })
        .catch((err) => console.log(err));
    } else {
      axios.post("http://localhost:8080/update-todo", { _id: isUpdating, text })
        .then((res) => {
          setTodos(todos.map(todo => todo._id === isUpdating ? res.data : todo));
          setText("");
          setUpdating("");
        })
        .catch((err) => console.log(err));
    }
  };

  const deleteToDo = (_id) => {
    axios.post("http://localhost:8080/delete-todo", { _id })
      .then(() => {
        setTodos(todos.filter(todo => todo._id !== _id));
      })
      .catch((err) => console.log(err));
  };

  const updateToDo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  };

  const toggleComplete = (_id) => {
    const todo = todos.find(todo => todo._id === _id);
    axios.post("http://localhost:8080/update-todo", { _id, completed: !todo.completed })
      .then((res) => {
        setTodos(todos.map(todo => todo._id === _id ? res.data : todo));
      })
      .catch((err) => console.log(err));
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === "completed") return todo.completed;
    if (filter === "pending") return !todo.completed;
    return true;
  });

  const totalTodos = todos.length;
  const completedTodos = todos.filter(todo => todo.completed).length;

  return (
    <div className='App'>
      <div className='container'>
        <h1>Place Your ToDo's Here....</h1>
        <div className='top'>
          <input
            type="text"
            required="required"
            placeholder='Write here before it forgets...'
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <button className='btn btn-outline-success' onClick={addOrUpdateTodo}>
            {isUpdating ? "Update" : "Add To-Do"}
          </button>
        </div>
        <div className='filters'>
          <button className={`btn btn-outline-primary ${filter === "all" && "active"}`} onClick={() => setFilter("all")}>All</button>
          <button className={`btn btn-outline-primary ${filter === "completed" && "active"}`} onClick={() => setFilter("completed")}>Completed</button>
          <button className={`btn btn-outline-primary ${filter === "pending" && "active"}`} onClick={() => setFilter("pending")}>Pending</button>
        </div>
        <div className='counts'>
          <span>Total: {totalTodos}</span> | <span>Completed: {completedTodos}</span>
        </div>
        <ul className='list-group list-group-flush'>
          {filteredTodos.map(todo =>
            <li key={todo._id} className='list-group-item'>
              <Todo
                text={todo.text}
                completed={todo.completed}
                timestamp={todo.timestamp}
                toggleComplete={() => toggleComplete(todo._id)}
                remove={() => deleteToDo(todo._id)}
                update={() => updateToDo(todo._id, todo.text)}
              />
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
