import React from 'react';

export default function Todo({ text, completed, timestamp, toggleComplete, remove, update }) {
  return (
    <div className={`todo ${completed ? 'completed' : ''}`}>
      <div className='text'>
        <input 
          type="checkbox" 
          checked={completed} 
          onChange={toggleComplete} 
          aria-label="Toggle todo completion"
        />
        <span 
          className={`todo-text ${completed ? 'completed' : ''}`} 
          onClick={toggleComplete}
          role="button"
          aria-pressed={completed}
        >
          {text}
        </span>
        <span className='timestamp'>{new Date(timestamp).toLocaleString()}</span>
      </div>
      <div className='icons'>
        <i 
          className="ri-edit-line" 
          id="update" 
          onClick={update} 
          role="button" 
          tabIndex={0} 
          aria-label="Edit todo"
        ></i>
        <i 
          className="ri-delete-bin-5-line" 
          id='delete' 
          onClick={remove} 
          role="button" 
          tabIndex={0} 
          aria-label="Delete todo"
        ></i>
      </div>
    </div>
  );
}
